     1→import React from 'react';
     2→import { z } from 'zod';
     3→import aui from '@/lib/aui';
     4→
     5→// ======================================
     6→// Simple tool - just 2 methods (no input schema needed)
     7→// ======================================
     8→const simpleTool = aui
     9→  .tool('weather')
    10→  .input(z.object({ city: z.string() }))
    11→  .execute(async ({ input }: { input: { city: string } }) => ({ temp: 72, city: input.city }))
    12→  .render(({ data }: { data: { temp: number; city: string } }) => <div>{data.city}: {data.temp}°</div>)
    13→  .build();
    14→
    15→// ======================================
    16→// Even simpler - sync execution
    17→// ======================================
    18→const syncTool = aui
    19→  .tool('greeting')
    20→  .input(z.object({ name: z.string() }))
    21→  .execute(({ input }: { input: { name: string } }) => `Hello, ${input.name}!`)
    22→  .render(({ data }: { data: string }) => <h1>{data}</h1>)
    23→  .done(); // alias for build()
    24→
    25→// ======================================
    26→// Complex tool - adds client optimization
    27→// ======================================
    28→const complexTool = aui
    29→  .tool('search')
    30→  .input(z.object({ query: z.string() }))
    31→  .execute(async ({ input }: { input: { query: string } }) => {
    32→    // Server-side: database search
    33→    return await db.search(input.query);
    34→  })
    35→  .client(async ({ input, ctx }: { input: { query: string }; ctx: any }) => {
    36→    // Client-side: check cache first, then fetch
    37→    const cached = ctx.cache.get(input.query);
    38→    return cached || ctx.fetch('/api/tools/search', { body: input });
    39→  })
    40→  .render(({ data }: { data: any }) => <SearchResults results={data} />)
    41→  .build();
    42→
    43→// ======================================
    44→// Server-only tool (no client execution)
    45→// ======================================
    46→const serverOnlyTool = aui
    47→  .tool('database-write')
    48→  .input(z.object({ 
    49→    table: z.string(),
    50→    data: z.record(z.any())
    51→  }))
    52→  .serverOnly()
    53→  .execute(async ({ input }: { input: { table: string; data: Record<string, any> } }) => {
    54→    // This will only run on server
    55→    return await db.insert(input.table, input.data);
    56→  })
    57→  .render(({ data }: { data: { id: number } }) => <div>Inserted: {data.id}</div>)
    58→  .build();
    59→
    60→// ======================================
    61→// Tool with description (for AI agents)
    62→// ======================================
    63→const aiTool = aui
    64→  .tool('stock-price')
    65→  .description('Get real-time stock price for a given ticker symbol')
    66→  .input(z.object({ ticker: z.string() }))
    67→  .execute(async ({ input }: { input: { ticker: string } }) => {
    68→    const price = await fetchStockPrice(input.ticker);
    69→    return { ticker: input.ticker, price };
    70→  })
    71→  .render(({ data }: { data: { ticker: string; price: number } }) => (
    72→    <div className="stock-card">
    73→      <span>{data.ticker}</span>
    74→      <span className="price">${data.price}</span>
    75→    </div>
    76→  ))
    77→  .build();
    78→
    79→// ======================================
    80→// Tool using context
    81→// ======================================
    82→const contextAwareTool = aui
    83→  .tool('user-profile')
    84→  .input(z.object({ userId: z.string().optional() }))
    85→  .execute(async ({ input, ctx }: { input: { userId?: string }; ctx?: any }) => {
    86→    const id = input.userId || ctx?.userId || 'anonymous';
    87→    return await fetchUserProfile(id);
    88→  })
    89→  .render(({ data }: { data: any }) => <UserProfile user={data} />)
    90→  .build();
    91→
    92→// ======================================
    93→// Register all tools
    94→// ======================================
    95→[simpleTool, syncTool, complexTool, serverOnlyTool, aiTool, contextAwareTool]
    96→  .forEach(tool => aui.register(tool));
    97→
    98→// ======================================
    99→// Usage in React component
   100→// ======================================
   101→export function ToolDemo() {
   102→  const [result, setResult] = React.useState<any>(null);
   103→  
   104→  const runTool = async () => {
   105→    // Mock executor for demo
   106→    const executor = {
   107→      execute: async (call: any) => ({ 
   108→        output: { temp: 72, city: call.input.city } 
   109→      })
   110→    };
   111→    const result = await executor.execute({
   112→      id: '1',
   113→      toolName: 'weather',
   114→      input: { city: 'San Francisco' }
   115→    });
   116→    setResult(result);
   117→  };
   118→
   119→  return (
   120→    <div>
   121→      <button onClick={runTool}>Run Weather Tool</button>
   122→      {result && simpleTool.render?.({ 
   123→        data: result.output, 
   124→        input: { city: 'San Francisco' }
   125→      })}
   126→    </div>
   127→  );
   128→}
   129→
   130→// Mock implementations
   131→const db = {
   132→  search: async (query: string) => [{ id: 1, title: `Result for ${query}` }],
   133→  insert: async (table: string, data: any) => ({ id: Date.now(), ...data })
   134→};
   135→
   136→const fetchStockPrice = async (ticker: string) => Math.random() * 1000;
   137→const fetchUserProfile = async (id: string) => ({ id, name: 'User ' + id });
   138→
   139→const SearchResults = ({ results }: any) => (
   140→  <div>{results.map((r: any) => <div key={r.id}>{r.title}</div>)}</div>
   141→);
   142→
   143→const UserProfile = ({ user }: any) => <div>{user.name}</div>;

