     1→import { createToolBuilder, tool as toolBuilder } from './core/builder';
     2→import { globalRegistry } from './core/registry';
     3→import type { ToolDefinition, ToolRegistry, ToolBuilder } from './types';
     4→import { z } from 'zod';
     5→import { ReactElement } from 'react';
     6→
     7→export * from './types';
     8→export { createRegistry } from './core/registry';
     9→export { ClientToolExecutor } from './client/executor';
    10→
    11→class AUI {
    12→  private registry: ToolRegistry;
    13→  
    14→  z = z;
    15→
    16→  constructor(registry?: ToolRegistry) {
    17→    this.registry = registry || globalRegistry;
    18→  }
    19→
    20→  tool(name: string) {
    21→    return createToolBuilder(name);
    22→  }
    23→
    24→  // Ultra-concise: create and auto-register a simple tool
    25→  simple<TInput, TOutput>(
    26→    name: string,
    27→    inputSchema: z.ZodType<TInput>,
    28→    handler: (input: TInput) => Promise<TOutput> | TOutput,
    29→    renderer?: (data: TOutput) => ReactElement
    30→  ): ToolDefinition<TInput, TOutput> {
    31→    const tool = this.tool(name)
    32→      .input(inputSchema)
    33→      .execute(handler as any);
    34→    
    35→    if (renderer) {
    36→      tool.render(renderer as any);
    37→    }
    38→    
    39→    const built = tool.build();
    40→    this.register(built);
    41→    return built;
    42→  }
    43→
    44→  // Quick mode: auto-build after execute and render
    45→  quick(name: string) {
    46→    return createToolBuilder(name).quick();
    47→  }
    48→
    49→  // Create a tool with full context support
    50→  contextual<TInput, TOutput>(
    51→    name: string,
    52→    inputSchema: z.ZodType<TInput>,
    53→    handler: (params: { input: TInput; ctx: any }) => Promise<TOutput> | TOutput,
    54→    renderer?: (data: TOutput) => ReactElement
    55→  ): ToolDefinition<TInput, TOutput> {
    56→    const tool = this.tool(name)
    57→      .input(inputSchema)
    58→      .execute(handler as any);
    59→    
    60→    if (renderer) {
    61→      tool.render(renderer as any);
    62→    }
    63→    
    64→    const built = tool.build();
    65→    this.register(built);
    66→    return built;
    67→  }
    68→
    69→  // Create a server-only tool (no client execution)
    70→  server<TInput, TOutput>(
    71→    name: string,
    72→    inputSchema: z.ZodType<TInput>,
    73→    handler: (input: TInput) => Promise<TOutput> | TOutput,
    74→    renderer?: (data: TOutput) => ReactElement
    75→  ): ToolDefinition<TInput, TOutput> {
    76→    const tool = this.tool(name)
    77→      .input(inputSchema)
    78→      .serverOnly()
    79→      .execute(handler as any);
    80→    
    81→    if (renderer) {
    82→      tool.render(renderer as any);
    83→    }
    84→    
    85→    const built = tool.build();
    86→    this.register(built);
    87→    return built;
    88→  }
    89→
    90→  register(tool: ToolDefinition) {
    91→    this.registry.register(tool);
    92→    return this;
    93→  }
    94→
    95→  getTools() {
    96→    return this.registry.list();
    97→  }
    98→
    99→  getTool(name: string) {
   100→    return this.registry.get(name);
   101→  }
   102→}
   103→
   104→// Create the global AUI instance
   105→const aui = new AUI();
   106→
   107→// Export everything
   108→export { aui, z, toolBuilder };
   109→export default aui;
   110→
   111→// Export type helpers for better DX
   112→export type Input<T> = T extends ToolDefinition<infer I, any> ? I : never;
   113→export type Output<T> = T extends ToolDefinition<any, infer O> ? O : never;

