    53→    handler: (params: { input: TInput; ctx: any }) => Promise<TOutput> | TOutput,
    54→    renderer?: (data: TOutput) => ReactElement
    55→  ): ToolDefinition<TInput, TOutput> {
    56→    const tool = this.tool(name)
    57→      .input(inputSchema)
    58→      .execute(handler as any);
    59→    
    60→    if (renderer) {
    61→      tool.render(renderer as any);
    62→    }
    63→    
    64→    const built = tool.build();
