    69→  // Create a server-only tool (no client execution)
    70→  server<TInput, TOutput>(
    71→    name: string,
    72→    inputSchema: z.ZodType<TInput>,
    73→    handler: (input: TInput) => Promise<TOutput> | TOutput,
    74→    renderer?: (data: TOutput) => ReactElement
    75→  ): ToolDefinition<TInput, TOutput> {
    76→    const tool = this.tool(name)
