import React from 'react';
import aui, { z, t, defineTool } from '../index';

// Method 1: Ultra-concise with t() helper
const weatherTool = t('weather')
  .in(z.object({ city: z.string() }))
  .ex(async (input) => ({ temp: 72, city: input.city }))
  .out((data) => <div>{data.city}: {data.temp}°</div>)
  .build();

// Method 2: One-liner with define()
const calculatorTool = t('calculator')
  .define(
    z.object({ a: z.number(), b: z.number(), op: z.enum(['+', '-', '*', '/']) }),
    async (input) => {
      switch (input.op) {
        case '+': return input.a + input.b;
        case '-': return input.a - input.b;
        case '*': return input.a * input.b;
        case '/': return input.a / input.b;
      }
    },
    (result) => <div>Result: {result}</div>
  );

// Method 3: Using defineTool helper function
const searchTool = defineTool('search', {
  input: z.object({ query: z.string(), limit: z.number().optional() }),
  execute: async (input) => {
    // Mock search
    const results = Array.from({ length: input.limit || 10 }, (_, i) => ({
      id: i,
      title: `Result ${i + 1} for "${input.query}"`,
      score: Math.random()
    }));
    return results.sort((a, b) => b.score - a.score);
  },
  client: async (input, ctx) => {
    const cached = ctx.cache.get(input.query);
    if (cached) return cached;

    const result = await ctx.fetch('/api/tools/search', {
      method: 'POST',
      body: JSON.stringify(input)
    });

    ctx.cache.set(input.query, result);
    return result;
  },
  render: (results) => (
    <div className="search-results">
      {results.map((item: any) => (
        <div key={item.id} className="result-item">
          <h3>{item.title}</h3>
          <span className="score">Score: {item.score.toFixed(2)}</span>
        </div>
      ))}
    </div>
  )
});

// Method 4: Batch definition for multiple tools
const aiTools = aui.defineTools({
  chat: {
    input: z.object({ message: z.string(), context: z.array(z.string()).optional() }),
    execute: async (input) => ({
      response: `You said: ${input.message}`,
      timestamp: new Date().toISOString()
    }),
    render: (data) => (
      <div className="chat-response">
        <p>{data.response}</p>
        <time>{data.timestamp}</time>
      </div>
    )
  },

  imageGen: {
    input: z.object({ prompt: z.string(), style: z.enum(['realistic', 'cartoon', 'abstract']) }),
    execute: async (input) => ({
      url: `https://placeholder.com/512x512?text=${encodeURIComponent(input.prompt)}`,
      prompt: input.prompt,
      style: input.style
    }),
    render: (data) => (
      <div className="generated-image">
        <img src={data.url} alt={data.prompt} />
        <p>Style: {data.style}</p>
      </div>
    )
  },

  dataAnalysis: {
    input: z.object({ 
      dataset: z.array(z.number()),
      operation: z.enum(['sum', 'avg', 'min', 'max', 'std'])
    }),
    execute: async (input) => {
      const { dataset, operation } = input;
      switch (operation) {
        case 'sum': return dataset.reduce((a, b) => a + b, 0);
        case 'avg': return dataset.reduce((a, b) => a + b, 0) / dataset.length;
        case 'min': return Math.min(...dataset);
        case 'max': return Math.max(...dataset);
        case 'std': {
          const avg = dataset.reduce((a, b) => a + b, 0) / dataset.length;
          const variance = dataset.reduce((sum, val) => sum + Math.pow(val - avg, 2), 0) / dataset.length;
          return Math.sqrt(variance);
        }
      }
    },
    render: (result) => (
      <div className="analysis-result">
        <strong>Result:</strong> {typeof result === 'number' ? result.toFixed(2) : result}
      </div>
    )
  }
});

// Method 5: Quick mode - auto-builds after render
const quickTool = aui
  .quick('quick-demo')
  .in(z.object({ text: z.string() }))
  .ex(async (input) => input.text.toUpperCase())
  .out((data) => <div className="uppercase">{data}</div>);
// No need to call .build() - it's automatic!

// Method 6: Simple helper for basic tools
const timestampTool = aui.simple(
  'timestamp',
  z.object({ format: z.enum(['iso', 'unix', 'locale']) }),
  (input) => {
    const now = new Date();
    switch (input.format) {
      case 'iso': return now.toISOString();
      case 'unix': return now.getTime();
      case 'locale': return now.toLocaleString();
    }
  },
  (timestamp) => <time>{timestamp}</time>
);

// Method 7: Server-only tool for secure operations
const dbTool = aui.server(
  'database',
  z.object({ 
    table: z.string(),
    operation: z.enum(['count', 'list', 'delete']),
    id: z.string().optional()
  }),
  async (input) => {
    // This runs only on the server, never exposed to client
    console.log('Secure DB operation:', input);
    return { success: true, operation: input.operation };
  },
  (result) => (
    <div className={`db-result ${result.success ? 'success' : 'error'}`}>
      Operation {result.operation} completed
    </div>
  )
);

// Export all tools
export {
  weatherTool,
  calculatorTool,
  searchTool,
  aiTools,
  quickTool,
  timestampTool,
  dbTool
};

import React from 'react';
import aui, { z, defineTool } from '../index';

/**
 * AI Control Tools - Enable AI assistants to control frontend and backend
 * These tools demonstrate how AI can interact with both client and server
 */

// Frontend Control Tool - AI can manipulate UI elements
const uiControlTool = defineTool('ui-control', {
  input: z.object({
    action: z.enum(['show', 'hide', 'toggle', 'update']),
    elementId: z.string(),
    data: z.any().optional()
  }),
  execute: async (input) => {
    // Server-side validation
    console.log('UI Control request:', input);
    return {
      action: input.action,
      elementId: input.elementId,
      timestamp: Date.now(),
      data: input.data
    };
  },
  client: async (input, ctx) => {
    // Client-side DOM manipulation
    const element = document.getElementById(input.elementId);
    if (!element) throw new Error(`Element ${input.elementId} not found`);

    switch (input.action) {
      case 'show':
        element.style.display = 'block';
        break;
      case 'hide':
        element.style.display = 'none';
        break;
      case 'toggle':
        element.style.display = element.style.display === 'none' ? 'block' : 'none';
        break;
      case 'update':
        if (input.data?.innerHTML) element.innerHTML = input.data.innerHTML;
        if (input.data?.className) element.className = input.data.className;
        if (input.data?.style) Object.assign(element.style, input.data.style);
        break;
    }

    return {
      action: input.action,
      elementId: input.elementId,
      success: true,
      timestamp: Date.now()
    };
  },
  render: (result) => (
    <div className="ui-control-result">
      ✓ {result.action} on #{result.elementId} completed
    </div>
  )
});

// Backend Control Tool - AI can execute server operations
const backendControlTool = defineTool('backend-control', {
  input: z.object({
    service: z.enum(['database', 'cache', 'queue', 'storage']),
    operation: z.string(),
    params: z.record(z.any())
  }),
  execute: async (input) => {
    // This runs on the server, AI can control backend services
    switch (input.service) {
      case 'database':
        // Mock database operations
        console.log('DB Operation:', input.operation, input.params);
        return { 
          service: 'database',
          result: `Executed ${input.operation} on database`,
          rowsAffected: Math.floor(Math.random() * 100)
        };

      case 'cache':
        // Mock cache operations
        console.log('Cache Operation:', input.operation, input.params);
        return {
          service: 'cache',
          result: `Cache ${input.operation} completed`,
          keys: Object.keys(input.params)
        };

      case 'queue':
        // Mock queue operations
        console.log('Queue Operation:', input.operation, input.params);
        return {
          service: 'queue',
          result: `Message queued for ${input.operation}`,
          queueLength: Math.floor(Math.random() * 50)
        };

      case 'storage':
        // Mock storage operations
        console.log('Storage Operation:', input.operation, input.params);
        return {
          service: 'storage',
          result: `File ${input.operation} completed`,
          size: Math.floor(Math.random() * 1000000)
        };
    }
  },
  render: (result) => (
    <div className="backend-result">
      <h4>{result.service} Operation</h4>
      <p>{result.result}</p>
      <pre>{JSON.stringify(result, null, 2)}</pre>
    </div>
  )
});

// Navigation Control - AI can navigate the app
const navigationTool = defineTool('navigate', {
  input: z.object({
    path: z.string(),
    params: z.record(z.string()).optional(),
    method: z.enum(['push', 'replace', 'back', 'forward']).default('push')
  }),
  execute: async (input) => {
    return {
      navigated: true,
      path: input.path,
      method: input.method,
      timestamp: Date.now()
    };
  },
  client: async (input, ctx) => {
    // Client-side navigation using Next.js router
    if (typeof window !== 'undefined') {
      const url = new URL(input.path, window.location.origin);

      if (input.params) {
        Object.entries(input.params).forEach(([key, value]) => {
          url.searchParams.set(key, value);
        });
      }

      switch (input.method) {
        case 'push':
          window.history.pushState({}, '', url.toString());
          break;
        case 'replace':
          window.history.replaceState({}, '', url.toString());
          break;
        case 'back':
          window.history.back();
          break;
        case 'forward':
          window.history.forward();
          break;
      }
    }

    return {
      navigated: true,
      path: input.path,
      method: input.method,
      timestamp: Date.now()
    };
  },
  render: (result) => (
    <div className="navigation-result">
      📍 Navigated to {result.path} via {result.method}
    </div>
  )
});

// Form Control - AI can fill and submit forms
const formControlTool = defineTool('form-control', {
  input: z.object({
    formId: z.string(),
    action: z.enum(['fill', 'submit', 'reset', 'validate']),
    fields: z.record(z.any()).optional()
  }),
  execute: async (input) => {
    // Server-side form validation
    if (input.action === 'validate' && input.fields) {
      const errors: Record<string, string> = {};

      // Mock validation
      Object.entries(input.fields).forEach(([key, value]) => {
        if (typeof value === 'string' && value.length < 3) {
          errors[key] = 'Too short';
        }
      });

      return {
        valid: Object.keys(errors).length === 0,
        errors,
        formId: input.formId
      };
    }

    return {
      action: input.action,
      formId: input.formId,
      success: true
    };
  },
  client: async (input, ctx) => {
    const form = document.getElementById(input.formId) as HTMLFormElement;
    if (!form) throw new Error(`Form ${input.formId} not found`);

    switch (input.action) {
      case 'fill':
        if (input.fields) {
          Object.entries(input.fields).forEach(([name, value]) => {
            const field = form.elements.namedItem(name) as HTMLInputElement;
            if (field) field.value = String(value);
          });
        }
        break;

      case 'submit':
        form.submit();
        break;

      case 'reset':
        form.reset();
        break;
    }

    return {
      action: input.action,
      formId: input.formId,
      success: true
    };
  },
  render: (result) => (
    <div className="form-control-result">
      {result.action === 'validate' ? (
        result.valid ? (
          <span className="valid">✓ Form is valid</span>
        ) : (
          <div className="errors">
            {Object.entries(result.errors || {}).map(([field, error]) => (
              <div key={field}>{field}: {error}</div>
            ))}
          </div>
        )
      ) : (
        <span>Form {result.action} completed</span>
      )}
    </div>
  )
});

// API Call Tool - AI can make API calls
const apiCallTool = defineTool('api-call', {
  input: z.object({
    endpoint: z.string(),
    method: z.enum(['GET', 'POST', 'PUT', 'DELETE', 'PATCH']),
    headers: z.record(z.string()).optional(),
    body: z.any().optional(),
    auth: z.object({
      type: z.enum(['bearer', 'basic', 'api-key']),
      credentials: z.string()
    }).optional()
  }),
  execute: async (input) => {
    // Server-side API call with authentication
    const headers: HeadersInit = {
      'Content-Type': 'application/json',
      ...input.headers
    };

    if (input.auth) {
      switch (input.auth.type) {
        case 'bearer':
          headers['Authorization'] = `Bearer ${input.auth.credentials}`;
          break;
        case 'basic':
          headers['Authorization'] = `Basic ${input.auth.credentials}`;
          break;
        case 'api-key':
          headers['X-API-Key'] = input.auth.credentials;
          break;
      }
    }

    try {
      const response = await fetch(input.endpoint, {
        method: input.method,
        headers,
        body: input.body ? JSON.stringify(input.body) : undefined
      });

      const data = await response.json();

      return {
        status: response.status,
        statusText: response.statusText,
        data,
        success: response.ok
      };
    } catch (error) {
      return {
        status: 0,
        statusText: 'Network Error',
        error: error instanceof Error ? error.message : 'Unknown error',
        success: false
      };
    }
  },
  render: (result) => (
    <div className={`api-result ${result.success ? 'success' : 'error'}`}>
      <div className="status">
        {result.status} {result.statusText}
      </div>
      <pre className="data">
        {JSON.stringify(result.data || result.error, null, 2)}
      </pre>
    </div>
  )
});

// State Management Tool - AI can manage application state
const stateManagementTool = defineTool('state-manage', {
  input: z.object({
    action: z.enum(['set', 'get', 'update', 'delete', 'clear']),
    key: z.string().optional(),
    value: z.any().optional(),
    namespace: z.string().default('global')
  }),
  execute: async (input) => {
    // Server-side state tracking
    return {
      action: input.action,
      namespace: input.namespace,
      key: input.key,
      timestamp: Date.now()
    };
  },
  client: async (input, ctx) => {
    // Client-side state management
    const stateKey = `${input.namespace}:${input.key || ''}`;

    switch (input.action) {
      case 'set':
        if (input.key && input.value !== undefined) {
          localStorage.setItem(stateKey, JSON.stringify(input.value));
          ctx.cache.set(stateKey, input.value);
        }
        break;

      case 'get':
        if (input.key) {
          const cached = ctx.cache.get(stateKey);
          if (cached) return { value: cached };

          const stored = localStorage.getItem(stateKey);
          if (stored) {
            const value = JSON.parse(stored);
            ctx.cache.set(stateKey, value);
            return { value };
          }
        }
        break;

      case 'update':
        if (input.key && input.value !== undefined) {
          const existing = ctx.cache.get(stateKey) || 
                          JSON.parse(localStorage.getItem(stateKey) || '{}');
          const updated = { ...existing, ...input.value };
          localStorage.setItem(stateKey, JSON.stringify(updated));
          ctx.cache.set(stateKey, updated);
          return { value: updated };
        }
        break;

      case 'delete':
        if (input.key) {
          localStorage.removeItem(stateKey);
          ctx.cache.delete(stateKey);
        }
        break;

      case 'clear':
        if (input.namespace === 'global') {
          localStorage.clear();
          ctx.cache.clear();
        } else {
          // Clear namespace
          for (let i = localStorage.length - 1; i >= 0; i--) {
            const key = localStorage.key(i);
            if (key?.startsWith(`${input.namespace}:`)) {
              localStorage.removeItem(key);
            }
          }
        }
        break;
    }

    return {
      action: input.action,
      namespace: input.namespace,
      key: input.key,
      success: true
    };
  },
  render: (result) => (
    <div className="state-result">
      State {result.action} {result.key ? `for ${result.key}` : ''} completed
      {result.value && (
        <pre>{JSON.stringify(result.value, null, 2)}</pre>
      )}
    </div>
  )
});

// Export all AI control tools
export const aiControlTools = {
  uiControl: uiControlTool,
  backendControl: backendControlTool,
  navigation: navigationTool,
  formControl: formControlTool,
  apiCall: apiCallTool,
  stateManagement: stateManagementTool
};

// Register all tools
Object.values(aiControlTools).forEach(tool => aui.register(tool));

export default aiControlTools;

import { describe, it, expect, beforeEach, vi } from 'vitest';
import React from 'react';
import aui, { z, t, defineTool } from '../index';
import { globalRegistry } from '../core/registry';

describe('Ultra-Concise AUI API', () => {
  beforeEach(() => {
    // Clear registry before each test
    globalRegistry.tools.clear();
  });

  describe('t() helper', () => {
    it('should create tools with shorthand methods', async () => {
      const tool = t('test')
        .in(z.object({ value: z.number() }))
        .ex(async (input) => input.value * 2)
        .out((data) => React.createElement('div', {}, data))
        .build();

      expect(tool.name).toBe('test');
      expect(tool.inputSchema.parse({ value: 5 })).toEqual({ value: 5 });

      const result = await tool.execute({ 
        input: { value: 5 }, 
        ctx: { cache: new Map(), fetch: vi.fn() } 
      });
      expect(result).toBe(10);
    });

    it('should support ultra-short aliases', async () => {
      const tool = t('calc')
        .in(z.object({ a: z.number(), b: z.number() }))
        .ex((input) => input.a + input.b)
        .build();

      const result = await tool.execute({
        input: { a: 3, b: 4 },
        ctx: { cache: new Map(), fetch: vi.fn() }
      });
      expect(result).toBe(7);
    });
  });

  describe('define() method', () => {
    it('should create and return a complete tool in one call', () => {
      const tool = t('one-liner')
        .define(
          z.object({ text: z.string() }),
          (input) => input.text.toUpperCase(),
          (data) => React.createElement('div', {}, data)
        );

      expect(tool.name).toBe('one-liner');
      expect(tool.execute).toBeDefined();
      expect(tool.render).toBeDefined();
    });
  });

  describe('defineTool() helper', () => {
    it('should create a tool with all options', async () => {
      const tool = defineTool('search', {
        input: z.object({ query: z.string() }),
        execute: async (input) => [`Result for ${input.query}`],
        client: async (input, ctx) => {
          ctx.cache.set(input.query, ['cached']);
          return ctx.cache.get(input.query);
        },
        render: (data) => React.createElement('div', {}, data.join(', '))
      });

      expect(tool.name).toBe('search');
      expect(tool.execute).toBeDefined();
      expect(tool.clientExecute).toBeDefined();
      expect(tool.render).toBeDefined();

      // Tool should be auto-registered
      expect(globalRegistry.get('search')).toBe(tool);
    });

    it('should work with minimal config', async () => {
      const tool = defineTool('minimal', {
        input: z.object({ x: z.number() }),
        execute: (input) => input.x * 2
      });

      const result = await tool.execute({
        input: { x: 5 },
        ctx: { cache: new Map(), fetch: vi.fn() }
      });
      expect(result).toBe(10);
    });
  });

  describe('aui.create()', () => {
    it('should create and register a tool', () => {
      const tool = aui.create('created', {
        input: z.object({ name: z.string() }),
        execute: (input) => `Hello, ${input.name}!`,
        render: (data) => React.createElement('span', {}, data)
      });

      expect(tool.name).toBe('created');
      expect(globalRegistry.get('created')).toBe(tool);
    });

    it('should support client execution', async () => {
      const mockFetch = vi.fn().mockResolvedValue({ data: 'fetched' });

      const tool = aui.create('with-client', {
        input: z.object({ id: z.string() }),
        execute: async (input) => ({ server: input.id }),
        client: async (input, ctx) => {
          const result = await ctx.fetch('/api/data', { body: input });
          return result;
        }
      });

      expect(tool.clientExecute).toBeDefined();

      if (tool.clientExecute) {
        const result = await tool.clientExecute({
          input: { id: '123' },
          ctx: { cache: new Map(), fetch: mockFetch }
        });
        expect(result).toEqual({ data: 'fetched' });
      }
    });
  });

  describe('aui.defineTools()', () => {
    it('should define multiple tools at once', () => {
      const tools = aui.defineTools({
        tool1: {
          input: z.object({ a: z.number() }),
          execute: (input) => input.a * 2
        },
        tool2: {
          input: z.object({ b: z.string() }),
          execute: (input) => input.b.toUpperCase(),
          render: (data) => React.createElement('div', {}, data)
        },
        tool3: {
          input: z.object({ c: z.boolean() }),
          execute: (input) => !input.c,
          client: async (input, ctx) => {
            ctx.cache.set('toggle', input.c);
            return !input.c;
          }
        }
      });

      expect(Object.keys(tools)).toHaveLength(3);
      expect(tools.tool1.name).toBe('tool1');
      expect(tools.tool2.name).toBe('tool2');
      expect(tools.tool3.name).toBe('tool3');

      // All should be registered
      expect(globalRegistry.get('tool1')).toBe(tools.tool1);
      expect(globalRegistry.get('tool2')).toBe(tools.tool2);
      expect(globalRegistry.get('tool3')).toBe(tools.tool3);
    });

    it('should handle empty object', () => {
      const tools = aui.defineTools({});
      expect(Object.keys(tools)).toHaveLength(0);
    });
  });

  describe('Quick mode', () => {
    it('should auto-build after render in quick mode', () => {
      const result = aui
        .quick('auto-build')
        .in(z.object({ text: z.string() }))
        .ex((input) => input.text)
        .out((data) => React.createElement('div', {}, data));

      // Should return a ToolDefinition, not a ToolBuilder
      expect(result.name).toBe('auto-build');
      expect(result.execute).toBeDefined();
      expect(result.render).toBeDefined();
    });
  });

  describe('Simple helper', () => {
    it('should create a simple tool with minimal config', async () => {
      const tool = aui.simple(
        'simple-test',
        z.object({ num: z.number() }),
        (input) => input.num * 3,
        (data) => React.createElement('span', {}, data)
      );

      expect(tool.name).toBe('simple-test');
      const result = await tool.execute({
        input: { num: 4 },
        ctx: { cache: new Map(), fetch: vi.fn() }
      });
      expect(result).toBe(12);

      // Should be registered
      expect(globalRegistry.get('simple-test')).toBe(tool);
    });

    it('should work without renderer', async () => {
      const tool = aui.simple(
        'no-render',
        z.object({ val: z.string() }),
        (input) => input.val.length
      );

      const result = await tool.execute({
        input: { val: 'hello' },
        ctx: { cache: new Map(), fetch: vi.fn() }
      });
      expect(result).toBe(5);
    });
  });

  describe('Server-only tools', () => {
    it('should create server-only tools', () => {
      const tool = aui.server(
        'secure',
        z.object({ secret: z.string() }),
        async (input) => ({ processed: true }),
        (data) => React.createElement('div', {}, 'Processed')
      );

      expect(tool.name).toBe('secure');
      expect(tool.isServerOnly).toBe(true);
      expect(tool.clientExecute).toBeUndefined();
    });
  });

  describe('Contextual tools', () => {
    it('should create tools with context support', async () => {
      const tool = aui.contextual(
        'with-context',
        z.object({ key: z.string() }),
        async ({ input, ctx }) => {
          ctx.cache.set(input.key, 'cached-value');
          return ctx.cache.get(input.key);
        },
        (data) => React.createElement('div', {}, data)
      );

      const cache = new Map();
      const result = await tool.execute({
        input: { key: 'test-key' },
        ctx: { cache, fetch: vi.fn() }
      });

      expect(result).toBe('cached-value');
      expect(cache.get('test-key')).toBe('cached-value');
    });
  });

  describe('Method chaining', () => {
    it('should support full method chaining', async () => {
      const tool = t('chained')
        .description('A chained tool')
        .metadata({ version: '1.0' })
        .in(z.object({ x: z.number(), y: z.number() }))
        .ex(({ x, y }) => ({ sum: x + y, product: x * y }))
        .output(z.object({ sum: z.number(), product: z.number() }))
        .out(({ sum, product }) => React.createElement('div', {}, `Sum: ${sum}, Product: ${product}`))
        .build();

      expect(tool.description).toBe('A chained tool');
      expect(tool.metadata).toEqual({ version: '1.0' });
      expect(tool.outputSchema).toBeDefined();

      const result = await tool.execute({
        input: { x: 3, y: 4 },
        ctx: { cache: new Map(), fetch: vi.fn() }
      });
      expect(result).toEqual({ sum: 7, product: 12 });
    });
  });

  describe('Edge cases', () => {
    it('should handle tools with no input', async () => {
      const tool = t('no-input')
        .ex(async () => ({ timestamp: Date.now() }))
        .build();

      const result = await tool.execute({
        input: {},
        ctx: { cache: new Map(), fetch: vi.fn() }
      });
      expect(result).toHaveProperty('timestamp');
    });

    it('should handle synchronous execution', async () => {
      const tool = t('sync')
        .in(z.object({ val: z.number() }))
        .ex((input) => input.val * 2) // Sync function
        .build();

      const result = await tool.execute({
        input: { val: 5 },
        ctx: { cache: new Map(), fetch: vi.fn() }
      });
      expect(result).toBe(10);
    });

    it('should throw error for tools without execute handler', () => {
      expect(() => {
        t('no-execute')
          .in(z.object({ x: z.number() }))
          .build();
      }).toThrow('Tool "no-execute" must have an execute handler');
    });
  });
});

