   323→// State Management Tool - AI can manage application state
   324→const stateManagementTool = defineTool('state-manage', {
   325→  input: z.object({
   326→    action: z.enum(['set', 'get', 'update', 'delete', 'clear']),
   327→    key: z.string().optional(),
   328→    value: z.any().optional(),
   329→    namespace: z.string().default('global')
   330→  }),
   331→  execute: async (input) => {
   332→    // Server-side state tracking
   333→    return {
   334→      action: input.action,
   335→      namespace: input.namespace,
   336→      key: input.key,
   337→      timestamp: Date.now()
   338→    };
   339→  },
   340→  client: async (input, ctx) => {
   341→    // Client-side state management
   342→    const stateKey = `${input.namespace}:${input.key || ''}`;
   343→    
   344→    switch (input.action) {
   345→      case 'set':
   346→        if (input.key && input.value !== undefined) {
   347→          localStorage.setItem(stateKey, JSON.stringify(input.value));
   348→          ctx.cache.set(stateKey, input.value);
   349→        }
   350→        break;
   351→      
   352→      case 'get':
   353→        if (input.key) {
   354→          const cached = ctx.cache.get(stateKey);
   355→          if (cached) return { value: cached };
   356→          
   357→          const stored = localStorage.getItem(stateKey);
   358→          if (stored) {
   359→            const value = JSON.parse(stored);
   360→            ctx.cache.set(stateKey, value);
   361→            return { value };
   362→          }
   363→        }
   364→        break;
   365→      
   366→      case 'update':
   367→        if (input.key && input.value !== undefined) {
   368→          const existing = ctx.cache.get(stateKey) || 
   369→                          JSON.parse(localStorage.getItem(stateKey) || '{}');
   370→          const updated = { ...existing, ...input.value };
   371→          localStorage.setItem(stateKey, JSON.stringify(updated));
   372→          ctx.cache.set(stateKey, updated);
   373→          return { value: updated };
   374→        }
   375→        break;
   376→      
   377→      case 'delete':
   378→        if (input.key) {
   379→          localStorage.removeItem(stateKey);
   380→          ctx.cache.delete(stateKey);
   381→        }
   382→        break;
   383→      
   384→      case 'clear':
   385→        if (input.namespace === 'global') {
   386→          localStorage.clear();
   387→          ctx.cache.clear();
   388→        } else {
   389→          // Clear namespace
   390→          for (let i = localStorage.length - 1; i >= 0; i--) {
   391→            const key = localStorage.key(i);
   392→            if (key?.startsWith(`${input.namespace}:`)) {
   393→              localStorage.removeItem(key);
   394→            }
   395→          }
   396→        }
   397→        break;
   398→    }
   399→    
   400→    return {
   401→      action: input.action,
   402→      namespace: input.namespace,
   403→      key: input.key,
   404→      success: true
   405→    };
   406→  },
   407→  render: (result) => (
   408→    <div className="state-result">
   409→      State {result.action} {result.key ? `for ${result.key}` : ''} completed
   410→      {result.value && (
   411→        <pre>{JSON.stringify(result.value, null, 2)}</pre>
   412→      )}
   413→    </div>
   414→  )
   415→});
   416→
   417→// Export all AI control tools
   418→export const aiControlTools = {
   419→  uiControl: uiControlTool,
   420→  backendControl: backendControlTool,
   421→  navigation: navigationTool,
   422→  formControl: formControlTool,
   423→  apiCall: apiCallTool,
   424→  stateManagement: stateManagementTool
   425→};
   426→
   427→// Register all tools
   428→Object.values(aiControlTools).forEach(tool => aui.register(tool));
   429→
   430→export default aiControlTools;

