     1→import { ReactElement } from 'react';
     2→import { z } from 'zod';
     3→import type {
     4→  AnyZodSchema,
     5→  ToolBuilder,
     6→  ToolContext,
     7→  ToolDefinition,
     8→  ToolExecuteParams,
     9→} from '../types';
    10→
    11→class ToolBuilderImpl<TInput = any, TOutput = any> implements ToolBuilder<TInput, TOutput> {
    12→  private config: Partial<ToolDefinition<TInput, TOutput>> = {};
    13→  private _quickMode = false;
    14→
    15→  constructor(name: string) {
    16→    this.config.name = name;
    17→    this.config.isServerOnly = false;
    18→  }
    19→
    20→  input<TSchema extends AnyZodSchema>(
    21→    schema: TSchema
    22→  ): ToolBuilder<z.infer<TSchema>, TOutput> {
    23→    this.config.inputSchema = schema;
    24→    return this as any;
    25→  }
    26→
    27→  execute<TNewOutput>(
    28→    handler: ((params: { input: TInput }) => Promise<TNewOutput> | TNewOutput) | 
    29→             ((params: { input: TInput; ctx?: ToolContext }) => Promise<TNewOutput> | TNewOutput) |
    30→             ((input: TInput) => Promise<TNewOutput> | TNewOutput)
    31→  ): ToolBuilder<TInput, TNewOutput> {
    32→    this.config.execute = async (params: ToolExecuteParams<TInput>) => {
    33→      // Smart parameter detection for cleaner API
    34→      const handlerStr = handler.toString();
    35→      const usesCtx = handlerStr.includes('ctx');
    36→      const isDestructured = handlerStr.includes('{') && handlerStr.indexOf('{') < handlerStr.indexOf('=>');
    37→      
    38→      let result;
    39→      if (!isDestructured && handler.length === 1) {
    40→        // Simple form: async (input) => { ... }
    41→        result = (handler as any)(params.input);
    42→      } else if (usesCtx) {
    43→        // With context: async ({ input, ctx }) => { ... }
    44→        result = (handler as any)(params);
    45→      } else {
    46→        // Destructured form: async ({ input }) => { ... }
    47→        result = (handler as any)({ input: params.input });
    48→      }
    49→      return Promise.resolve(result);
    50→    };
    51→    
    52→    // Auto-build in quick mode
    53→    if (this._quickMode && this.config.render) {
    54→      return this.build() as any;
    55→    }
    56→    return this as any;
    57→  }
    58→
    59→  // Shorthand: combine execute and render for ultra-concise API
    60→  run<TNewOutput>(
    61→    handler: ((input: TInput) => Promise<TNewOutput> | TNewOutput),
    62→    renderer?: ((data: TNewOutput) => ReactElement)
    63→  ): ToolBuilder<TInput, TNewOutput> | ToolDefinition<TInput, TNewOutput> {
    64→    this.execute(handler as any);
    65→    if (renderer) {
    66→      this.render(renderer as any);
    67→      if (this._quickMode) {
    68→        return this.build() as any;
    69→      }
    70→    }
    71→    return this as any;
    72→  }
    73→
    74→  clientExecute(
    75→    handler: ((params: { input: TInput; ctx: ToolContext }) => Promise<TOutput> | TOutput) |
    76→             ((input: TInput, ctx: ToolContext) => Promise<TOutput> | TOutput)
    77→  ): ToolBuilder<TInput, TOutput> {
    78→    this.config.clientExecute = async (params) => {
    79→      const result = handler.length === 2
    80→        ? (handler as any)(params.input, params.ctx)
    81→        : (handler as any)(params);
    82→      return Promise.resolve(result);
    83→    };
    84→    this.config.isServerOnly = false;
    85→    return this;
    86→  }
    87→
    88→  client(
    89→    handler: ((params: { input: TInput; ctx: ToolContext }) => Promise<TOutput> | TOutput) |
    90→             ((input: TInput, ctx: ToolContext) => Promise<TOutput> | TOutput)
    91→  ): ToolBuilder<TInput, TOutput> {
    92→    return this.clientExecute(handler);
    93→  }
    94→  
    95→  // Ultra-concise alias
    96→  clientEx(
    97→    handler: ((params: { input: TInput; ctx: ToolContext }) => Promise<TOutput> | TOutput) |
    98→             ((input: TInput, ctx: ToolContext) => Promise<TOutput> | TOutput)
    99→  ): ToolBuilder<TInput, TOutput> {
   100→    return this.clientExecute(handler);
   101→  }
   102→
   103→  serverOnly(): ToolBuilder<TInput, TOutput> {
   104→    this.config.isServerOnly = true;
   105→    return this;
   106→  }
   107→
   108→  render(
   109→    component: ((props: { data: TOutput; input: TInput }) => ReactElement) |
   110→               ((data: TOutput) => ReactElement)
   111→  ): ToolBuilder<TInput, TOutput> {
   112→    this.config.render = component.length === 1
   113→      ? (props: any) => (component as any)(props.data)
   114→      : component as any;
   115→    
   116→    // Auto-build in quick mode if execute is already set
   117→    if (this._quickMode && this.config.execute) {
   118→      return this.build() as any;
   119→    }
   120→    return this;
   121→  }
   122→
   123→  // Ultra-concise: set both input and execute in one go
   124→  handle<TNewInput, TNewOutput>(
   125→    inputSchema: z.ZodType<TNewInput>,
   126→    handler: ((input: TNewInput) => Promise<TNewOutput> | TNewOutput)
   127→  ): ToolBuilder<TNewInput, TNewOutput> {
   128→    this.input(inputSchema as any);
   129→    this.execute(handler as any);
   130→    return this as any;
   131→  }
   132→
   133→  // Chain input, execute, and render in one call
   134→  define<TNewInput, TNewOutput>(
   135→    inputSchema: z.ZodType<TNewInput>,
   136→    handler: ((input: TNewInput) => Promise<TNewOutput> | TNewOutput),
   137→    renderer?: ((data: TNewOutput) => ReactElement)
   138→  ): ToolDefinition<TNewInput, TNewOutput> {
   139→    this.input(inputSchema as any);
   140→    this.execute(handler as any);
   141→    if (renderer) {
   142→      this.render(renderer as any);
   143→    }
   144→    return this.build() as any;
   145→  }
   146→
   147→  // Shorthand aliases for common patterns
   148→  in<TSchema extends AnyZodSchema>(
   149→    schema: TSchema
   150→  ): ToolBuilder<z.infer<TSchema>, TOutput> {
   151→    return this.input(schema);
   152→  }
   153→
   154→  ex<TNewOutput>(
   155→    handler: ((input: TInput) => Promise<TNewOutput> | TNewOutput)
   156→  ): ToolBuilder<TInput, TNewOutput> {
   157→    return this.execute(handler as any);
   158→  }
   159→
   160→  out(component: ((data: TOutput) => ReactElement)): ToolBuilder<TInput, TOutput> {
   161→    return this.render(component as any);
   162→  }
   163→
   164→  // Enable quick mode for auto-building
   165→  quick(): ToolBuilder<TInput, TOutput> {
   166→    this._quickMode = true;
   167→    return this;
   168→  }
   169→
   170→  description(desc: string): ToolBuilder<TInput, TOutput> {
   171→    this.config.description = desc;
   172→    return this;
   173→  }
   174→
   175→  metadata(meta: Record<string, any>): ToolBuilder<TInput, TOutput> {
   176→    this.config.metadata = { ...this.config.metadata, ...meta };
   177→    return this;
   178→  }
   179→
   180→  output<TSchema extends AnyZodSchema>(
   181→    schema: TSchema
   182→  ): ToolBuilder<TInput, z.infer<TSchema>> {
   183→    this.config.outputSchema = schema;
   184→    return this as any;
   185→  }
   186→
   187→  build(): ToolDefinition<TInput, TOutput> {
   188→    if (!this.config.inputSchema) {
   189→      this.config.inputSchema = z.object({}) as any;
   190→    }
   191→    if (!this.config.execute) {
   192→      throw new Error(`Tool "${this.config.name}" must have an execute handler`);
   193→    }
   194→    if (!this.config.render) {
   195→      this.config.render = ({ data }) => data as any;
   196→    }
   197→
   198→    return this.config as ToolDefinition<TInput, TOutput>;
   199→  }
   200→
   201→  // Alias for build
   202→  create(): ToolDefinition<TInput, TOutput> {
   203→    return this.build();
   204→  }
   205→
   206→  done(): ToolDefinition<TInput, TOutput> {
   207→    return this.build();
   208→  }
   209→}
   210→
   211→export function createToolBuilder(name: string): ToolBuilder {
   212→  return new ToolBuilderImpl(name);
   213→}
   214→
   215→// Export shortcuts for ultra-concise API
   216→export const tool = createToolBuilder;
   217→export const t = createToolBuilder;

