    28→
    29→  // Ultra-concise: create and auto-register a simple tool
    30→  simple<TInput, TOutput>(
    31→    name: string,
    32→    inputSchema: z.ZodType<TInput>,
    33→    handler: (input: TInput) => Promise<TOutput> | TOutput,
    34→    renderer?: (data: TOutput) => ReactElement
    35→  ): ToolDefinition<TInput, TOutput> {
    36→    const tool = this.tool(name)
    37→      .input(inputSchema)
    38→      .execute(handler as any);
    39→    
    40→    if (renderer) {
    41→      tool.render(renderer as any);
    42→    }
    43→    
    44→    const built = tool.build();
    45→    this.register(built);
