    47→  }
    48→
    49→  // One-liner tool creation with automatic registration
    50→  create<TInput, TOutput>(
    51→    name: string,
    52→    config: {
    53→      input: z.ZodType<TInput>;
    54→      execute: (input: TInput) => Promise<TOutput> | TOutput;
    55→      render?: (data: TOutput) => ReactElement;
    56→      client?: (input: TInput, ctx: ToolContext) => Promise<TOutput> | TOutput;
    57→    }
    58→  ): ToolDefinition<TInput, TOutput> {
    59→    const tool = this.tool(name)
    60→      .input(config.input)
    61→      .execute(config.execute as any);
    62→    
    63→    if (config.client) {
    64→      tool.clientExecute(config.client as any);
    65→    }
    66→    
    67→    if (config.render) {
    68→      tool.render(config.render as any);
    69→    }
    70→    
    71→    const built = tool.build();
    72→    this.register(built);
    73→    return built;
    74→  }
    75→
    76→  // Quick mode: auto-build after execute and render
    77→  quick(name: string) {
