    79→  }
    80→
    81→  // Create a tool with full context support
    82→  contextual<TInput, TOutput>(
    83→    name: string,
    84→    inputSchema: z.ZodType<TInput>,
    85→    handler: (params: { input: TInput; ctx: any }) => Promise<TOutput> | TOutput,
    86→    renderer?: (data: TOutput) => ReactElement
    87→  ): ToolDefinition<TInput, TOutput> {
    88→    const tool = this.tool(name)
