   100→
   101→  // Create a server-only tool (no client execution)
   102→  server<TInput, TOutput>(
   103→    name: string,
   104→    inputSchema: z.ZodType<TInput>,
   105→    handler: (input: TInput) => Promise<TOutput> | TOutput,
   106→    renderer?: (data: TOutput) => ReactElement
   107→  ): ToolDefinition<TInput, TOutput> {
   108→    const tool = this.tool(name)
