   106→          const avg = dataset.reduce((a, b) => a + b, 0) / dataset.length;
   107→          const variance = dataset.reduce((sum, val) => sum + Math.pow(val - avg, 2), 0) / dataset.length;
   108→          return Math.sqrt(variance);
   109→        }
   110→      }
   111→    },
   112→    render: (result) => (
   113→      <div className="analysis-result">
   114→        <strong>Result:</strong> {typeof result === 'number' ? result.toFixed(2) : result}
   115→      </div>
   116→    )
   117→  }
   118→});
   119→
   120→// Method 5: Quick mode - auto-builds after render
   121→const quickTool = aui
   122→  .quick('quick-demo')
   123→  .in(z.object({ text: z.string() }))
   124→  .ex(async (input) => input.text.toUpperCase())
   125→  .out((data) => <div className="uppercase">{data}</div>);
   126→// No need to call .build() - it's automatic!
   127→
   128→// Method 6: Simple helper for basic tools
   129→const timestampTool = aui.simple(
   130→  'timestamp',
   131→  z.object({ format: z.enum(['iso', 'unix', 'locale']) }),
   132→  (input) => {
   133→    const now = new Date();
   134→    switch (input.format) {
   135→      case 'iso': return now.toISOString();
   136→      case 'unix': return now.getTime();
   137→      case 'locale': return now.toLocaleString();
   138→    }
   139→  },
   140→  (timestamp) => <time>{timestamp}</time>
   141→);
   142→
   143→// Method 7: Server-only tool for secure operations
   144→const dbTool = aui.server(
   145→  'database',
   146→  z.object({ 
   147→    table: z.string(),
   148→    operation: z.enum(['count', 'list', 'delete']),
   149→    id: z.string().optional()
   150→  }),
   151→  async (input) => {
   152→    // This runs only on the server, never exposed to client
   153→    console.log('Secure DB operation:', input);
   154→    return { success: true, operation: input.operation };
   155→  },
   156→  (result) => (
   157→    <div className={`db-result ${result.success ? 'success' : 'error'}`}>
   158→      Operation {result.operation} completed
   159→    </div>
   160→  )
   161→);
   162→
