import React from 'react';
import aui, { z } from '../index';
import { globalRegistry } from '../core/registry';

describe('Enhanced AUI API', () => {
  beforeEach(() => {
    globalRegistry.tools.clear();
  });

  describe('do() method', () => {
    it('should create tool with just a function', async () => {
      const tool = aui.do('simple-do', async (input: string) => input.toUpperCase());

      expect(tool.name).toBe('simple-do');
      expect(globalRegistry.get('simple-do')).toBe(tool);

      const result = await tool.execute({
        input: 'hello' as any,
        ctx: { cache: new Map(), fetch: jest.fn() }
      });
      expect(result).toBe('HELLO');
    });

    it('should create tool with object configuration', async () => {
      const tool = aui.do('configured-do', {
        input: z.object({ num: z.number() }),
        execute: async (input) => input.num * 2,
        render: (data) => React.createElement('div', {}, data)
      });

      expect(tool.name).toBe('configured-do');
      expect(tool.inputSchema).toBeDefined();
      expect(tool.render).toBeDefined();

      const result = await tool.execute({
        input: { num: 5 },
        ctx: { cache: new Map(), fetch: jest.fn() }
      });
      expect(result).toBe(10);
    });

    it('should support client execution in do()', async () => {
      const mockCtx = { cache: new Map(), fetch: jest.fn() };

      const tool = aui.do('client-do', {
        input: z.object({ key: z.string() }),
        execute: async (input) => ({ server: input.key }),
        client: async (input, ctx) => {
          ctx.cache.set(input.key, 'cached');
          return { client: ctx.cache.get(input.key) };
        }
      });

      expect(tool.clientExecute).toBeDefined();

      if (tool.clientExecute) {
        const result = await tool.clientExecute({
          input: { key: 'test' },
          ctx: mockCtx
        });
        expect(result).toEqual({ client: 'cached' });
        expect(mockCtx.cache.get('test')).toBe('cached');
      }
    });
  });

  describe('ai() method', () => {
    it('should create AI-optimized tool with retry logic', async () => {
      let attempts = 0;
      const tool = aui.ai('ai-retry', {
        input: z.object({ fail: z.number() }),
        execute: async (input) => {
          attempts++;
          if (attempts < input.fail) {
            throw new Error('Simulated failure');
          }
          return { attempts };
        },
        retry: 3
      });

      expect(tool.metadata?.aiOptimized).toBe(true);
      expect(tool.metadata?.retry).toBe(3);

      // Should succeed after retries
      const result = await tool.execute({
        input: { fail: 2 },
        ctx: { cache: new Map(), fetch: jest.fn() }
      });
      expect(result).toEqual({ attempts: 2 });
    });

    it('should fail after max retries', async () => {
      let attempts = 0;
      const tool = aui.ai('ai-fail', {
        execute: async () => {
          attempts++;
          throw new Error('Always fails');
        },
        retry: 2
      });

      await expect(tool.execute({
        input: {},
        ctx: { cache: new Map(), fetch: jest.fn() }
      })).rejects.toThrow('Always fails');

      expect(attempts).toBe(2); // Should have tried twice
    });

    it('should store AI metadata', () => {
      const tool = aui.ai('ai-meta', {
        execute: async () => ({}),
        retry: 5,
        timeout: 10000,
        cache: true
      });

      expect(tool.metadata).toEqual({
        aiOptimized: true,
        retry: 5,
        timeout: 10000,
        cache: true
      });
    });
  });

  describe('aiTools() batch creation', () => {
    it('should create multiple AI tools at once', () => {
      const tools = aui.aiTools({
        tool1: {
          input: z.object({ x: z.number() }),
          execute: async (input) => input.x * 2,
          retry: 3
        },
        tool2: {
          execute: async () => 'hello',
          cache: true
        }
      });

      expect(Object.keys(tools)).toHaveLength(2);
      expect(tools.tool1.metadata?.aiOptimized).toBe(true);
      expect(tools.tool1.metadata?.retry).toBe(3);
      expect(tools.tool2.metadata?.cache).toBe(true);

      // Should be registered
      expect(globalRegistry.get('tool1')).toBe(tools.tool1);
      expect(globalRegistry.get('tool2')).toBe(tools.tool2);
    });
  });

  describe('Single character aliases', () => {
    it('should support ultra-short syntax', async () => {
      const tool = aui.t('ultra')
        .i(z.object({ n: z.number() }))
        .e(async (input) => input.n * 3)
        .r((data) => React.createElement('span', {}, data))
        .b();

      expect(tool.name).toBe('ultra');

      const result = await tool.execute({
        input: { n: 4 },
        ctx: { cache: new Map(), fetch: jest.fn() }
      });
      expect(result).toBe(12);
    });

    it('should support client with c() alias', async () => {
      const tool = aui.t('client-alias')
        .i(z.object({ val: z.string() }))
        .e(async (input) => input.val)
        .c(async (input, ctx) => {
          ctx.cache.set('key', input.val);
          return input.val.toUpperCase();
        })
        .b();

      expect(tool.clientExecute).toBeDefined();

      const ctx = { cache: new Map(), fetch: jest.fn() };
      if (tool.clientExecute) {
        const result = await tool.clientExecute({
          input: { val: 'test' },
          ctx
        });
        expect(result).toBe('TEST');
        expect(ctx.cache.get('key')).toBe('test');
      }
    });
  });

  describe('Tool builder do() method', () => {
    it('should handle function-only form', async () => {
      const tool = aui.t('builder-do')
        .do(async (input: number) => input * 2);

      const result = await tool.execute({
        input: 5 as any,
        ctx: { cache: new Map(), fetch: jest.fn() }
      });
      expect(result).toBe(10);
    });

    it('should handle object configuration form', async () => {
      const tool = aui.t('builder-do-config')
        .do({
          input: z.object({ text: z.string() }),
          execute: async (input) => input.text.length,
          render: (data) => React.createElement('div', {}, data)
        });

      expect(tool.inputSchema).toBeDefined();
      expect(tool.render).toBeDefined();

      const result = await tool.execute({
        input: { text: 'hello' },
        ctx: { cache: new Map(), fetch: jest.fn() }
      });
      expect(result).toBe(5);
    });
  });

  describe('Tool builder ai() method', () => {
    it('should create AI-optimized tool from builder', async () => {
      let attempts = 0;
      const tool = aui.t('builder-ai')
        .ai({
          input: z.object({ threshold: z.number() }),
          execute: async (input) => {
            attempts++;
            if (attempts < input.threshold) {
              throw new Error('Not ready');
            }
            return { success: true, attempts };
          },
          retry: 5,
          timeout: 1000
        });

      expect(tool.metadata?.aiOptimized).toBe(true);
      expect(tool.metadata?.retry).toBe(5);
      expect(tool.metadata?.timeout).toBe(1000);

      const result = await tool.execute({
        input: { threshold: 3 },
        ctx: { cache: new Map(), fetch: jest.fn() }
      });
      expect(result).toEqual({ success: true, attempts: 3 });
    });
  });

  describe('Global AI mode', () => {
    it('should set global AI options', () => {
      aui.setAIMode(true, {
        retry: 5,
        timeout: 10000,
        cache: true
      });

      expect((aui as any).aiModeEnabled).toBe(true);
      expect((aui as any).aiOptions).toEqual({
        retry: 5,
        timeout: 10000,
        cache: true
      });
    });

    it('should disable AI mode', () => {
      aui.setAIMode(false);

      expect((aui as any).aiModeEnabled).toBe(false);
    });
  });

  describe('Edge cases', () => {
    it('should handle empty input in do()', async () => {
      const tool = aui.do('no-input', async () => 'result');

      const result = await tool.execute({
        input: undefined as any,
        ctx: { cache: new Map(), fetch: jest.fn() }
      });
      expect(result).toBe('result');
    });

    it('should handle sync functions in ai()', async () => {
      const tool = aui.ai('sync-ai', {
        execute: (input: number) => input * 2  // Sync function
      });

      const result = await tool.execute({
        input: 5 as any,
        ctx: { cache: new Map(), fetch: jest.fn() }
      });
      expect(result).toBe(10);
    });

    it('should handle builder chaining with new methods', () => {
      const tool = aui.t('chain')
        .description('Chained tool')
        .metadata({ version: '2.0' })
        .i(z.object({ x: z.number() }))
        .e((input) => input.x)
        .r((data) => React.createElement('div', {}, data))
        .b();

      expect(tool.description).toBe('Chained tool');
      expect(tool.metadata?.version).toBe('2.0');
    });
  });
});

