   204→  }
   205→
   206→  done(): ToolDefinition<TInput, TOutput> {
   207→    return this.build();
   208→  }
   209→
   210→  // Ultra-concise: single method to do everything
   211→  do<TNewInput = TInput, TNewOutput = TOutput>(
   212→    handler: ((input: TNewInput) => Promise<TNewOutput> | TNewOutput) |
   213→             { 
   214→               input?: z.ZodType<TNewInput>;
   215→               execute: (input: TNewInput) => Promise<TNewOutput> | TNewOutput;
   216→               render?: (data: TNewOutput) => ReactElement;
   217→               client?: (input: TNewInput, ctx: ToolContext) => Promise<TNewOutput> | TNewOutput;
   218→             }
   219→  ): ToolDefinition<TNewInput, TNewOutput> {
   220→    if (typeof handler === 'function') {
   221→      // Simple function form
