   228→      this.execute(handler.execute as any);
   229→      if (handler.client) {
   230→        this.clientExecute(handler.client as any);
   231→      }
   232→      if (handler.render) {
   233→        this.render(handler.render as any);
   234→      }
   235→    }
   236→    return this.build() as any;
   237→  }
   238→
   239→  // AI-optimized method for tool calls
   240→  ai<TNewInput = TInput, TNewOutput = TOutput>(
   241→    config: {
   242→      input?: z.ZodType<TNewInput>;
   243→      execute: (input: TNewInput) => Promise<TNewOutput> | TNewOutput;
   244→      client?: (input: TNewInput, ctx: ToolContext) => Promise<TNewOutput> | TNewOutput;
   245→      render?: (data: TNewOutput) => ReactElement;
   246→      retry?: number;
   247→      timeout?: number;
   248→      cache?: boolean;
