     4→export interface ExecutorOptions {
     5→  context?: Partial<ToolContext>;
     6→  registry?: { get: (name: string) => any };
     7→}
     8→
     9→export class ToolExecutor {
    10→  private context: ToolContext;
    11→  private registry: { get: (name: string) => any };
    12→
    13→  constructor(options: ExecutorOptions = {}) {
    14→    this.context = {
    15→      cache: new Map(),
    16→      fetch: globalThis.fetch,
    17→      ...options.context,
    18→    };
    19→    this.registry = options.registry || globalRegistry;
    20→  }
    21→
    22→  async execute(toolCall: ToolCall): Promise<ToolResult> {
    23→    const tool = this.registry.get(toolCall.toolName);
    24→    
    25→    if (!tool) {
    26→      return {
    27→        id: toolCall.id,
    28→        toolName: toolCall.toolName,
    29→        output: null,
    30→        error: `Tool "${toolCall.toolName}" not found`,
    31→      };
    32→    }
    33→
    34→    try {
    35→      const validatedInput = tool.inputSchema.parse(toolCall.input);
    36→      const output = await tool.execute({
    37→        input: validatedInput,
    38→        ctx: this.context,
    39→      });
    40→
    41→      return {
    42→        id: toolCall.id,
    43→        toolName: toolCall.toolName,
    44→        output,
    45→      };
    46→    } catch (error) {
    47→      return {
    48→        id: toolCall.id,
    49→        toolName: toolCall.toolName,
    50→        output: null,
    51→        error: error instanceof Error ? error.message : String(error),
    52→      };
    53→    }
    54→  }
    55→
    56→  async executeBatch(toolCalls: ToolCall[]): Promise<ToolResult[]> {
    57→    return Promise.all(toolCalls.map(call => this.execute(call)));
    58→  }
    59→}
    60→
    61→export const defaultExecutor = new ToolExecutor();

