    26→
    27→  private getCacheKey(toolName: string, input: any): string {
    28→    return `${toolName}:${JSON.stringify(input)}`;
    29→  }
    30→
    31→  private getFromCache(key: string): any | null {
    32→    const cached = this.cache.get(key);
    33→    if (!cached) return null;
    34→
    35→    const now = Date.now();
    36→    if (now - cached.timestamp > this.cacheTimeout) {
    37→      this.cache.delete(key);
    38→      return null;
    39→    }
    40→
    41→    return cached.data;
    42→  }
    43→
    44→  private setCache(key: string, data: any): void {
    45→    this.cache.set(key, {
    46→      data,
    47→      timestamp: Date.now(),
    48→    });
    49→  }
    50→
    51→  async execute(toolCall: ToolCall): Promise<ToolResult> {
    52→    const tool = this.tools.get(toolCall.toolName);
    53→    
    54→    if (!tool) {
    55→      return this.executeRemote(toolCall);
    56→    }
    57→
    58→    if (tool.clientExecute) {
    59→      const cacheKey = this.getCacheKey(toolCall.toolName, toolCall.input);
    60→      const cached = this.getFromCache(cacheKey);
    61→      
    62→      if (cached !== null) {
    63→        return {
    64→          id: toolCall.id,
    65→          toolName: toolCall.toolName,
    66→          output: cached,
    67→        };
    68→      }
    69→
    70→      try {
    71→        const context: ToolContext = {
    72→          cache: new Map(),
    73→          fetch: (url: string, options?: RequestInit) => {
    74→            return fetch(url, options).then(res => res.json());
    75→          },
    76→        };
    77→
    78→        const output = await tool.clientExecute({
    79→          input: toolCall.input,
    80→          ctx: context,
    81→        });
    82→
    83→        this.setCache(cacheKey, output);
    84→
    85→        return {
    86→          id: toolCall.id,
    87→          toolName: toolCall.toolName,
    88→          output,
    89→        };
    90→      } catch (error) {
    91→        return {
    92→          id: toolCall.id,
    93→          toolName: toolCall.toolName,
    94→          output: null,
    95→          error: error instanceof Error ? error.message : String(error),
    96→        };
    97→      }
    98→    }
    99→
   100→    return this.executeRemote(toolCall);
   101→  }
   102→
   103→  private async executeRemote(toolCall: ToolCall): Promise<ToolResult> {
   104→    try {
   105→      const response = await fetch(this.apiUrl, {
   106→        method: 'POST',
   107→        headers: {
   108→          'Content-Type': 'application/json',
   109→        },
   110→        body: JSON.stringify({ toolCall }),
   111→      });
   112→
   113→      if (!response.ok) {
   114→        throw new Error(`HTTP error! status: ${response.status}`);
   115→      }
   116→
   117→      return await response.json();
   118→    } catch (error) {
   119→      return {
   120→        id: toolCall.id,
   121→        toolName: toolCall.toolName,
   122→        output: null,
   123→        error: error instanceof Error ? error.message : String(error),
   124→      };
   125→    }
   126→  }
   127→
   128→  async executeBatch(toolCalls: ToolCall[]): Promise<ToolResult[]> {
   129→    const localCalls: ToolCall[] = [];
   130→    const remoteCalls: ToolCall[] = [];
   131→    const results: Map<string, ToolResult> = new Map();
   132→
   133→    for (const call of toolCalls) {
   134→      const tool = this.tools.get(call.toolName);
   135→      if (tool?.clientExecute) {
   136→        localCalls.push(call);
   137→      } else {
   138→        remoteCalls.push(call);
   139→      }
   140→    }
   141→
   142→    if (localCalls.length > 0) {
   143→      const localResults = await Promise.all(
   144→        localCalls.map(call => this.execute(call))
   145→      );
   146→      localResults.forEach(result => results.set(result.id, result));
   147→    }
   148→
   149→    if (remoteCalls.length > 0) {
   150→      try {
   151→        const response = await fetch(this.apiUrl, {
   152→          method: 'POST',
   153→          headers: {
   154→            'Content-Type': 'application/json',
   155→          },
   156→          body: JSON.stringify({ toolCalls: remoteCalls }),
   157→        });
   158→
   159→        if (!response.ok) {
   160→          throw new Error(`HTTP error! status: ${response.status}`);
   161→        }
   162→
   163→        const { results: remoteResults } = await response.json();
   164→        remoteResults.forEach((result: ToolResult) => results.set(result.id, result));
   165→      } catch (error) {
   166→        remoteCalls.forEach(call => {
   167→          results.set(call.id, {
   168→            id: call.id,
   169→            toolName: call.toolName,
   170→            output: null,
   171→            error: error instanceof Error ? error.message : String(error),
   172→          });
   173→        });
   174→      }
   175→    }
   176→
   177→    return toolCalls.map(call => results.get(call.id)!);
   178→  }
   179→
   180→  clearCache(): void {
   181→    this.cache.clear();
   182→  }
   183→}

