     1→import { NextRequest, NextResponse } from 'next/server';
     2→import { ToolExecutor } from '@/lib/aui/server/executor';
     3→import { globalRegistry } from '@/lib/aui/core/registry';
     4→import { registerDefaultTools } from '@/lib/aui/tools';
     5→import type { ToolCall } from '@/lib/aui/types';
