     9→
    10→const executor = new ToolExecutor({
    11→  context: {
    12→    cache: new Map(),
    13→    fetch: globalThis.fetch,
    14→  },
    15→  registry: globalRegistry,
    16→});
    17→
    18→export async function POST(request: NextRequest) {
    19→  try {
    20→    const body = await request.json();
    21→    
    22→    if (body.toolCalls) {
    23→      const toolCalls: ToolCall[] = body.toolCalls;
    24→      const results = await executor.executeBatch(toolCalls);
    25→      return NextResponse.json({ results });
    26→    }
    27→    
    28→    if (!body.toolCall) {
    29→      return NextResponse.json(
    30→        { error: 'Missing toolCall or toolCalls in request body' },
    31→        { status: 400 }
    32→      );
    33→    }
    34→
    35→    const toolCall: ToolCall = body.toolCall;
    36→    const result = await executor.execute(toolCall);
    37→
    38→    return NextResponse.json(result);
    39→  } catch (error) {
    40→    console.error('Tool execution error:', error);
    41→    return NextResponse.json(
    42→      { error: 'Failed to execute tool', details: error instanceof Error ? error.message : String(error) },
    43→      { status: 500 }
    44→    );
    45→  }
    46→}
    47→
    48→export async function GET() {
    49→  const tools = globalRegistry.list().map(tool => ({
    50→    name: tool.name,
    51→    description: tool.description,
    52→    inputSchema: tool.inputSchema._def,
    53→    outputSchema: tool.outputSchema?._def,
    54→    isServerOnly: tool.isServerOnly,
    55→    metadata: tool.metadata,
    56→  }));
    57→
    58→  return NextResponse.json({ tools, count: tools.length });
    59→}

