    37→  },
    38→  client: async (input, ctx) => {
    39→    const cached = ctx.cache.get(input.query);
    40→    if (cached) return cached;
    41→    
    42→    const result = await ctx.fetch('/api/tools/search', {
    43→      method: 'POST',
    44→      body: JSON.stringify(input)
    45→    });
