    91→  },
    92→  
    93→  dataAnalysis: {
    94→    input: z.object({ 
    95→      dataset: z.array(z.number()),
    96→      operation: z.enum(['sum', 'avg', 'min', 'max', 'std'])
    97→    }),
    98→    execute: async (input) => {
    99→      const { dataset, operation } = input;
   100→      switch (operation) {
   101→        case 'sum': return dataset.reduce((a, b) => a + b, 0);
   102→        case 'avg': return dataset.reduce((a, b) => a + b, 0) / dataset.length;
   103→        case 'min': return Math.min(...dataset);
   104→        case 'max': return Math.max(...dataset);
   105→        case 'std': {
   106→          const avg = dataset.reduce((a, b) => a + b, 0) / dataset.length;
   107→          const variance = dataset.reduce((sum, val) => sum + Math.pow(val - avg, 2), 0) / dataset.length;
   108→          return Math.sqrt(variance);
   109→        }
   110→      }
   111→    },
   112→    render: (result) => (
